let form=document.getElementById("formulario"),totalNumeros=[],linhas="";function adicionarLinha(){var e=document.getElementById("nome"),t=document.getElementById("telefone"),a=(totalNumeros.push(parseFloat(e.value)),"<tr>"),a=(a+=`<td>${e.value}</td>`)+`<td>${t.value}</td>`+"</tr>";linhas+=a,e.value="",t.value=""}function atualizaTabela(){document.querySelector("tbody").innerHTML=linhas}function contaLinhasTabela(){var e=document.getElementById("tabela").getElementsByTagName("tr");document.getElementById("quantidade").innerHTML=e.length}form.addEventListener("submit",function(e){e.preventDefault(),adicionarLinha(),atualizaTabela(),contaLinhasTabela()});